import"./style.css";import api from"./modules/api.js";const displayMovies=async()=>{const e=document.getElementById("list-of-shows"),t=document.querySelector(".popup-comments");e.innerHTML="";const n=await fetch(api.moviesApi),s=await n.json();n.ok?s.forEach((n=>{const s=document.createElement("section");s.classList.add("show_items");const a=document.createElement("div");a.classList.add("show_image");const i=document.createElement("div");i.innerHTML=` <img class="movie_image" src="${n.image.medium}" alt="${n.name}">`;const o=document.createElement("div");o.classList.add("actions_name");const c=document.createElement("p");c.innerHTML=`${n.name}`,c.classList.add("movie");const m=document.createElement("div");m.classList.add("icon");const d=document.createElement("span");d.classList.add("clickable"),document.createElement("i").innerHTML='<i class="fa-solid fa-heart"></i>';const l=document.createElement("p");l.innerText="2 Likes";const p=document.createElement("div");p.classList.add("comment-button");const r=document.createElement("button");r.innerText="Comments",r.classList.add("button"),p.append(r),m.append(d,l),o.append(c,m),a.append(i),s.append(a,o,p),e.append(s),r.addEventListener("click",(()=>{t.innerHTML=`\n        <div class="popIt">\n        <div class="flex pop column">\n          <span class="close-button">&times;</span>\n          <div class="popup_image">\n            <img class="pop_image" src="${n.image.medium}" alt="asdf">\n          </div>\n          <p class="pop_meal">Name: ${n.name}</p>\n          <p>Ratings: ${n.rating.average}</p>\n          <p class="instructions">Airdate: ${n.airdate}</p>\n          <p>Runtime: ${n.runtime}</p>\n          <div class="comment_count"></div>\n          <button class="view_more">Refresh Comments</button>\n          <div class="pop_comment"></div>\n          <div id="commentList"></div>\n          <h3 class="add-comm">Add a comment</h3>\n          <div class="inputDiv">\n            <form class="form">\n              <input id="name" type="text" name="user" required placeholder="Your Name"><br>\n              <textarea id="text" type="text" rows="6" name="text" required placeholder="Your insight"></textarea><br>\n              <div>\n                <button class="submit-btn" type="submit">Comment</button>\n              </div>\n            </form>\n          </div>\n        </div>\n      </div>\n      `,t.style.display="flex",document.body.style.overflow="hidden"}))})):e.innerText="Server Down"};displayMovies();