import"./style.css";import api from"./modules/api.js";import addLike from"./modules/like.js";const displayMovies=async()=>{const e=document.getElementById("list-of-shows"),t=document.querySelector(".popup-comments");e.innerHTML="";const n=await fetch(api.moviesApi),s=await n.json();if(!n.ok)return void(e.innerText="Server Down");console.log("data",s);const i=await fetch(`${api.likesUrl}`),a=await i.json();console.log("Get likes",a),s.forEach((e=>{a.forEach((t=>{e.id===t.item_id&&(e.likes=t.likes)}))})),s.forEach((n=>{const s=document.createElement("section");s.classList.add("show_items"),s.setAttribute("id",n.id);const i=document.createElement("div");i.classList.add("show_image");const a=document.createElement("div");a.innerHTML=` <img class="movie_image" src="${n.image.medium}" alt="${n.name}">`;const o=document.createElement("div");o.classList.add("actions_name");const d=document.createElement("p");d.innerHTML=`${n.name}`,d.classList.add("movie");let c=n.likes||0;const m=document.createElement("div");m.classList.add("icon");const l=document.createElement("span");l.classList.add("clickable");const r=document.createElement("i");r.innerHTML='<i class="fa-solid fa-heart"></i>';const p=document.createElement("p");p.innerText=`${c} Likes`,r.addEventListener("click",(async()=>{await addLike(n.id)})),console.log("updatedLikesCount");const u=document.createElement("div");u.classList.add("comment-button");const v=document.createElement("button");v.innerText="Comments",v.classList.add("button"),u.append(v),m.append(l,r,p),o.append(d,m),i.append(a),s.append(i,o,u),e.append(s),v.addEventListener("click",(()=>{t.innerHTML=`\n        <div class="popIt">\n\n        <div class="pop">\n\n          <span class="close-button">&times;</span>\n\n            <div class="popup_image">\n            <img class="pop_image" src="${n.image.medium}" alt="asdf">\n            </div>\n\n               <p class="pop_meal">Name: ${n.name}</p>\n               <p>Ratings: ${n.rating.average}</p>\n               <p class="instructions">Airdate: ${n.airdate}</p>\n               <p>Runtime: ${n.runtime}</p>\n\n                 <div class="comment_count"></div>\n\n          <button class="view_more">Refresh Comments</button>\n         \n          <div class="pop_comment"></div>\n          <div id="commentList"></div>\n\n          <h3 class="add-comm">Add a comment</h3>\n\n          <div class="inputDiv">\n            <form class="form">\n              <input id="name" type="text" name="user" required placeholder="Your Name"><br>\n              <textarea id="text" type="text" rows="6" name="text" required placeholder="Your insight"></textarea><br>\n              <div>\n                <button class="submit-btn" type="submit">Comment</button>\n              </div>\n            </form>\n           </div>\n\n        </div>\n\n      </div>\n      `,t.style.display="flex",document.body.style.overflow="hidden"}))}))};displayMovies();