import"./style.css";import api from"./modules/api.js";const displayMovies=async()=>{const e=document.getElementById("list-of-shows"),n=document.querySelector(".popup-comments");e.innerHTML="";const t=await fetch(api.moviesApi),s=await t.json();t.ok?s.forEach((t=>{const s=document.createElement("section");s.classList.add("show_items");const a=document.createElement("div");a.classList.add("show_image");const i=document.createElement("div");i.innerHTML=` <img class="movie_image" src="${t.image.medium}" alt="${t.name}">`;const c=document.createElement("div");c.classList.add("actions_name");const o=document.createElement("p");o.innerHTML=`${t.name}`,o.classList.add("movie");const m=document.createElement("div");m.classList.add("icon");const d=document.createElement("span");d.classList.add("clickable"),document.createElement("i").innerHTML='<i class="fa-solid fa-heart"></i>';const l=document.createElement("p");l.innerText="2 Likes";const p=document.createElement("div");p.classList.add("comment-button");const r=document.createElement("button");r.innerText="Comments",r.classList.add("button"),p.append(r),m.append(d,l),c.append(o,m),a.append(i),s.append(a,c,p),e.append(s),r.addEventListener("click",(()=>{n.innerHTML=`\n        <div class="popIt">\n          <div class ="flex pop column">\n            <span class="close-button">&times;</span>\n              <div class="popup_image">\n                <img class="pop_image" src="${t.image.medium}" alt="asdf">\n              </div>\n                 <p class="pop_meal">Name: ${t.name}</p> \n                 <p> Ratings: ${t.rating.average}</p>\n                 <p class="instructions">Airdate${t.airdate}<p>\n                 <p> Runtime: ${t.runtime}</p>\n              <div class="comment_count">\n              </div>\n              <button class="view_more">Refresh Comments</button>\n              <div class="pop_comment">\n            \n              </div>\n              <div id ="commentList"></div>\n              <h3 class="add-comm">Add a comment</h3>\n            <div class="inputDiv">\n              <form class="form">\n                <input id="name"  type="text" name="user" required placeholder="Your Name"><br>\n                <textarea id="text" type="text" rows=6 name="text" required placeholder="Your insight"></textarea><br>\n                <div>\n                <button class="submit-btn" type="submit">Comment</button>\n              </div>\n              </form>\n              \n            </div>\n          </div>\n        </div>`,n.style.display="flex",document.body.style.display="none"}))})):e.innerText="Server Down"};displayMovies();